func (m *SettingModel) Paginate(ctx context.Context, page int64, perPage int64, builders ...query.SQLBuilder) ([]SettingN, query.PaginateMeta, error) {
	if page <= 0 {
		page = 1
	}

	if perPage <= 0 {
		perPage = 15
	}

	meta := query.PaginateMeta{
		PerPage: perPage,
		Page:    page,
	}

	count, err := m.Count(ctx, builders...)
	if err != nil {
		return nil, meta, err
	}

	meta.Total = count
	meta.LastPage = count / perPage
	if count%perPage != 0 {
		meta.LastPage += 1
	}

	res, err := m.Get(ctx, append([]query.SQLBuilder{query.Builder().Limit(perPage).Offset((page - 1) * perPage)}, builders...)...)
	if err != nil {
		return res, meta, err
	}

	return res, meta, nil
}

// Get retrieve all results for given query
func (m *SettingModel) Get(ctx context.Context, builders ...query.SQLBuilder) ([]SettingN, error) {
	b := m.query.Merge(builders...).Table(m.tableName).AppendCondition(m.applyScope())
	if len(b.GetFields()) == 0 {
		b = b.Select(
			"id",
			"key",
			"value",
			"created_at",
			"updated_at",
		)
	}

	fields := b.GetFields()
	selectFields := make([]query.Expr, 0)

	for _, f := range fields {
		switch strcase.ToSnake(f.Value) {

		case "id":
			selectFields = append(selectFields, f)
		case "key":
			selectFields = append(selectFields, f)
		case "value":
			selectFields = append(selectFields, f)
		case "created_at":
			selectFields = append(selectFields, f)
		case "updated_at":
			selectFields = append(selectFields, f)
		}
	}

	var createScanVar = func(fields []query.Expr) (*SettingN, []interface{}) {
		var settingVar SettingN
		scanFields := make([]interface{}, 0)

		for _, f := range fields {
			switch strcase.ToSnake(f.Value) {

			case "id":
				scanFields = append(scanFields, &settingVar.Id)
			case "key":
				scanFields = append(scanFields, &settingVar.Key)
			case "value":
				scanFields = append(scanFields, &settingVar.Value)
			case "created_at":
				scanFields = append(scanFields, &settingVar.CreatedAt)
			case "updated_at":
				scanFields = append(scanFields, &settingVar.UpdatedAt)
			}
		}

		return &settingVar, scanFields
	}

	sqlStr, params := b.Fields(selectFields...).ResolveQuery()

	rows, err := m.db.QueryContext(ctx, sqlStr, params...)
	if err != nil {
		return nil, err
	}

	defer rows.Close()

	settings := make([]SettingN, 0)
	for rows.Next() {
		settingReal, scanFields := createScanVar(fields)
		if err := rows.Scan(scanFields...); err != nil {
			return nil, err
		}

		settingReal.original = &settingOriginal{}
		_ = query.Copy(settingReal, settingReal.original)

		settingReal.SetModel(m)
		settings = append(settings, *settingReal)
	}

	return settings, nil
}

